package com.example.saller.utilsimport androidx.compose.foundation.BorderStrokeimport androidx.compose.foundation.Imageimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.borderimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.*import androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.Iconimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.material3.TextFieldimport androidx.compose.runtime.*import androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.Shapeimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.unit.dpimport com.example.saller.Rimport com.example.saller.presentor.homeScreen.ProductContract@OptIn(ExperimentalMaterial3Api::class)@Composablefun SimpleSearchItem(    modifier: Modifier = Modifier,    value: String,    onValueChange: (String) -> Unit,    placeholder: String = "Поиск",    shape: Shape = MaterialTheme.shapes.small,) {    var isSearching by remember { mutableStateOf(false) }    Box(        modifier = modifier            .fillMaxWidth()    ) {        TextField(            value = value,            onValueChange = {                onValueChange(it)                isSearching = it.isNotEmpty()            },            textStyle = MaterialTheme.typography.bodySmall.copy(color = Color.Black),            placeholder = { Text(text = placeholder, color = Color.Gray) },            singleLine = true,            modifier = Modifier                .fillMaxWidth()                .padding(16.dp),            trailingIcon = {                Icon(painter = painterResource(id = R.drawable.search),                    contentDescription = "search",modifier=Modifier.size(30.dp))            }        )    }}